    public static ArrayList<Camera> getLisData() {
        Camera camera = null;
        ArrayList<Camera> list = new ArrayList<>();
        for (int i = 0; i < camerasData.length; i++) {
            camera = new Camera();
            camera.setName(camerasData[i][0]);
            camera.setType(camerasData[i][1]);
            camera.setPrice(camerasData[i][2]);
            camera.setDesc(camerasData[i][3]);
            camera.setPhoto(camerasData[i][4]);
            list.add(camera);
        }
        return list;
    }
